export const CTable = forwardRef<HTMLTableElement, CTableProps>(
  (
    {
      children,
      align,
      borderColor,
      bordered,
      borderless,
      caption,
      captionTop,
      className,
      color,
      columns,
      footer,
      hover,
      items,
      responsive,
      small,
      striped,
      stripedColumns,
      tableFootProps,
      tableHeadProps,
