    },
    ref,
  ) => {
    const columnNames = useMemo(() => getColumnNames(columns, items), [columns, items])

    return (
      <CTableResponsiveWrapper responsive={responsive}>
        <table
          className={classNames(
            'table',
            {
              [`align-${align}`]: align,
              [`border-${borderColor}`]: borderColor,
              [`caption-top`]: captionTop || caption === 'top',
