import React, { useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Accordion from '@mui/material/Accordion';
import AccordionSummary from '@mui/material/AccordionSummary';
import AccordionDetails from '@mui/material/AccordionDetails';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ImageHome from '../../images/HomeImage.jpg';

const LearnMore = (props) => {
    // set state
  const [books, setBooks] = useState([]);

  // first data grab
    useEffect(() => {
      fetch("http://localhost:3001/books?title="+props.title) // your url may look different
        .then(resp => resp.json())
        .then(data => setBooks(data)) // set data to state
    }, []); 

  return (
    <div>
        <br/><br/>
        {/* iterate through the books array and render a unique book component for each book object in the array */}
        <Grid container rowSpacing={4} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
        { books.map(book => 
        <Grid item xs={4}>
        <Card sx={{ maxWidth: 400, height: 400 }}>
      <CardMedia 
        sx={{ width: 400, height: 200, alignItems: 'center' }}
        image = { ImageHome }
        title="Book1"
      />
      <CardContent sx={{ textAlign: 'center' }}>
        <Typography gutterBottom variant="h5" component="div">
          { book.title }
        </Typography>
        <Accordion>
        <AccordionSummary expandIcon={<ExpandMoreIcon />} aria-controls="panel1a-content" id="panel1a-header">
          <Typography>Description</Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Typography>
          { book.longDescription }
          </Typography>
        </AccordionDetails>
      </Accordion>
      </CardContent>
      <CardActions >
        <Box textAlign='center'>
            <Button size="small">Add to Cart</Button>
            <Button size="small">Learn More</Button>
        </Box>
      </CardActions>
    </Card>
    </Grid>
    ) }
    </Grid>
    <br/><br/>
    </div>
  );
}

export default LearnMore;
