import React, { useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import {useLocation} from 'react-router-dom';
import HomeImage from '../../images/HomeImage.jpg';
import USerContext from '../../Context/UserContext';

export default function LearnMore() {

  const location = useLocation();
    // set state
  const [books, setBooks] = useState([]);

  // first data grab
  useEffect(() => {
    let id = location.state.id;
    fetch('http://localhost:3001/books?id='+id) // your url may look different
    .then(resp => resp.json())
    .then(data => setBooks(data)) // set data to state
  }, [location.state.id]); 

return (
    <div>
        <br/><br/>
        <USerContext.Provider value={books}>
        {/* iterate through the books array and render a unique book component for each book object in the array */}
        <Grid alignItems="center" justifyContent="center" container rowSpacing={4} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
        { books.map(book => 
        <Grid alignItems="center" justifyContent="center" item xs={4}>
        <Card sx={{ width: 1, height: 800, margin: "0 auto", padding: "0.1em" }}>
        <CardMedia 
        component="img"
        sx={{ width: 400, height: 200, alignItems: 'center', padding: "1em 1em 0 1em", objectFit: "contain" }}
        image = { HomeImage }
        title="Book1"
      />
      <CardContent sx={{ textAlign: 'center' }}>
        <Typography gutterBottom variant="h5" component="div">
          { book.title }
        </Typography>
        <Typography gutterBottom variant="label" component="div">
          { book.longDescription }
        </Typography> 
      </CardContent>
    </Card>
    </Grid> )}
    </Grid>
    </USerContext.Provider>
    <br/><br/>
    </div>
  );
}
