import axios from "axios"

const url = "http://localhost:3001/UserDetails"
const urlOrders = "http://localhost:3001/Orders";
const urlFavourites= "http://localhost:3001/Favourites";
const urlCartList = "http://localhost:3001/Cart";
const urlBooksList = "http://localhost:3001/books";

let booksListData = [];

export const UserLogin = (user) => {
    return axios.get(url, {
        params: {
            email: user.email,
            password: user.password,
            role: user.role
        }
    })
}

export const UserRegister = (newUser) => {
    return axios.post(url, newUser);
}

export const orderHistory = (user) => {
    return axios.get(urlOrders, {
        params: {
            email: user.email,
            role: user.role
        }
    })
}

export const booksList = () => {
    return axios.get(urlBooksList);
}

export const favouritesList = (user) => {
    return axios.get(urlFavourites, {
        params: {
            email: user.email,
            role: user.role
        }
    })
}

export const cartList = (user) => {
    return axios.get(urlCartList, {
        params: {
            email: user.email,
            role: user.role
        }
    })
}

export const addTocart = (title) => {
    booksListData = booksList();
    console.log(booksListData);
    let bookData = booksListData.find(book => {
		if (book.title === title) {
            console.log(book);
			return book;
		}
		return null;
	});
    console.log(bookData);
    if (bookData) {
        return axios.post(urlCartList, bookData);
    } else {
        console.log('Entered book data is not present in db');
        return Promise.reject(new Error('Entered book data is not present in db'));
    }
}
